							mbtn_view_ci_OnMenuItem(strName, nItemID)
{
  	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_contractci,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_contractci,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

							TabContract_OnTabSelected(nTab)
{
	var tSLA = 2;
	var tServ = 7;
	var tabAuditTrail = 8;
		
	if(nTab==tSLA)
	{
		var strSLAs = app.g.get_contract_slas(_swdoc.oContract().pk_contract_id);
		app._slf(_swdoc.MF().sl_slas, "swc=pk_slad_id&kvs="+strSLAs);
	}
	
	if(nTab==tServ)
	{
		var strServs = app.g.get_contract_services(_swdoc.oContract().pk_contract_id);
		app._slf(_swdoc.MF().sl_slas, "swc=fk_cmdb_id&kvs="+strServs);
	}
	
	if(nTab==tabAuditTrail)
	{
		mainform.sl_contract_diary.Refresh();
	}

}

							mbtn_sla_actions_OnMenuItem(strName, nItemID)
{
	var iAddSLA = 1;
	var iDeleteSLA = 2;
	
	//-- Add Contract
	if(nItemID == iAddSLA)
	{
		app.OpenForm("browse_slas", "mode=select", true, function(x)
		{
			var strKeys = x.document.strSelectedKey;
			if(strKeys=="")return;
			var arrKeys = strKeys.split(",");
			for (var x=0; x< arrKeys.length;x++)
			{
				var fk_key = arrKeys[x];
				if(app.g.sl_findrow_byvalue(sl_slas,fk_key,"pk_slad_id")==-1)
				{
            		var strRel = "Uses";
            		var recSLA = app.g.get_record("itsmsp_slad", fk_key);                               
					app.cmdb.generate_meconfigrelation(_swdoc.oContract().fk_cmdb_id,recSLA.fk_cmdb_id,strRel);  		
				}
						
			}
			var strSLAs = app.g.get_contract_slas(_swdoc.oContract().pk_contract_id);
			app._slf(_swdoc.MF().sl_slas, "swc=pk_slad_id&kvs="+strSLAs);
				
			sl_slas.Refresh();
			sl_slas.SetRowSelected(0);
			sl_slas_OnItemSelected(0);
		});
	}
	else if(nItemID == iDeleteSLA)
	{
		//-- 15.12.2004
		//-- NWJ
		//-- delete contract
		var strSLAID = app.g.sl_getrawcolvalue(sl_slas,-1,"pk_slad_id");
		if (strSLAID!="")
		{

			if (!confirm("Are you sure you want to dis-associate the selected SLA?")) return;
			
			var strSLACMDBID = app.g.sl_getcolvalue(sl_slas, -1, "fk_cmdb_id");
			app.cmdb.delete_meconfigrelation(_swdoc.oContract().fk_cmdb_id,strSLACMDBID);
			var strSLAs = app.g.get_contract_slas(_swdoc.oContract().pk_contract_id);
			app._slf(_swdoc.MF().sl_slas, "swc=pk_slad_id&kvs="+strSLAs);
				
			sl_slas.Refresh();
			sl_slas.SetRowSelected(0);
			sl_slas_OnItemSelected(0);
			
		}
	}
	


}
